mapper.select:
SELECT * FROM project
WHERE groupName = #{groupName}
  AND companyName = #{companyName}

mapper.insert:
INSERT INTO project (
    projectNo, companyName, groupName, `system`,
    contactPerson, contactEmail, hourlyRate,
    contractHours, promisedHours, consumedHours
) VALUES (
             #{projectNo}, #{companyName}, #{groupName}, #{system},
             #{contactPerson}, #{contactEmail}, #{hourlyRate},
             #{contractHours}, #{promisedHours}, #{consumedHours}
         )
